using UnityEngine;


///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////


public class AutoСontroller : MonoBehaviour
{
    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////


    public float speedMove = 1;
    public Transform smog;

    //--------------

    Transform tr;
    RaycastHit hit;


    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////


    void Awake()
    {
        //--------------

        tr = transform;

        //--------------
    }


    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////


    void Update()
    {
        //--------------

        tr.Translate(Vector3.right * speedMove * Time.deltaTime, Space.Self);

        //--------------
    }


    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////


    void LateUpdate()
    {
        //--------------

        if (Physics.Raycast(new Vector3(smog.position.x, smog.position.y + 2, smog.position.z), -tr.up, out hit, 100))
            smog.position = new Vector3(smog.position.x, hit.point.y, smog.position.z);

        //--------------
    }



    ///////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
}
